INSERT OR REPLACE INTO tarot_cards (id, name_zh, name_en, suit, number, image_url, keywords_zh, keywords_en, meaning_upright_zh, meaning_upright_en, meaning_reversed_zh, meaning_reversed_en, element, astrological_sign, planet, description_zh, description_en) VALUES ('the_fool', '愚者', 'The Fool', 'major', 0, '/images/tarot/major/fool.jpg', '["新开始","冒险","纯真","自由"]', '["New Beginnings","Adventure","Innocence","Freedom"]', '新的开始，充满可能性的旅程，纯真的心灵，勇敢的冒险精神。', 'New beginnings, a journey full of possibilities, innocent heart, brave adventurous spirit.', '鲁莽行事，缺乏计划，过度天真，逃避责任。', 'Reckless behavior, lack of planning, excessive naivety, avoiding responsibility.', 'Air', 'Uranus', 'Uranus', '愚者代表着新的开始和无限的可能性。他象征着纯真的心灵和勇敢的冒险精神，提醒我们要保持开放的心态去迎接生活中的新挑战。', 'The Fool represents new beginnings and infinite possibilities. He symbolizes an innocent heart and brave adventurous spirit, reminding us to keep an open mind to embrace new challenges in life.');
INSERT OR REPLACE INTO tarot_cards (id, name_zh, name_en, suit, number, image_url, keywords_zh, keywords_en, meaning_upright_zh, meaning_upright_en, meaning_reversed_zh, meaning_reversed_en, element, astrological_sign, planet, description_zh, description_en) VALUES ('the_magician', '魔术师', 'The Magician', 'major', 1, '/images/tarot/major/magician.jpg', '["意志力","技能","专注","行动"]', '["Willpower","Skill","Focus","Action"]', '拥有实现目标的能力，专注的意志力，技能和资源的运用，主动的行动。', 'Ability to achieve goals, focused willpower, use of skills and resources, proactive action.', '缺乏意志力，技能不足，滥用权力，欺骗他人。', 'Lack of willpower, insufficient skills, abuse of power, deceiving others.', 'Air', 'Mercury', 'Mercury', '魔术师象征着意志力和实现目标的能力。他提醒我们拥有实现梦想所需的所有工具和技能，关键在于如何运用它们。', 'The Magician symbolizes willpower and the ability to achieve goals. He reminds us that we have all the tools and skills needed to realize our dreams, the key is how to use them.');
INSERT OR REPLACE INTO tarot_cards (id, name_zh, name_en, suit, number, image_url, keywords_zh, keywords_en, meaning_upright_zh, meaning_upright_en, meaning_reversed_zh, meaning_reversed_en, element, astrological_sign, planet, description_zh, description_en) VALUES ('the_high_priestess', '女祭司', 'The High Priestess', 'major', 2, '/images/tarot/major/high_priestess.jpg', '["直觉","神秘","潜意识","智慧"]', '["Intuition","Mystery","Subconscious","Wisdom"]', '强大的直觉力，内在的智慧，神秘的知识，潜意识的洞察。', 'Strong intuitive power, inner wisdom, mysterious knowledge, subconscious insight.', '缺乏直觉，忽视内在声音，过度理性，隐藏真相。', 'Lack of intuition, ignoring inner voice, excessive rationality, hiding truth.', 'Water', 'Moon', 'Moon', '女祭司代表着直觉和内在智慧。她提醒我们要倾听内心的声音，相信自己的直觉，从潜意识中获取指引。', 'The High Priestess represents intuition and inner wisdom. She reminds us to listen to our inner voice, trust our intuition, and seek guidance from the subconscious.');
INSERT OR REPLACE INTO tarot_cards (id, name_zh, name_en, suit, number, image_url, keywords_zh, keywords_en, meaning_upright_zh, meaning_upright_en, meaning_reversed_zh, meaning_reversed_en, element, astrological_sign, planet, description_zh, description_en) VALUES ('the_empress', '皇后', 'The Empress', 'major', 3, '/images/tarot/major/empress.jpg', '["丰盛","母性","创造力","自然"]', '["Abundance","Motherhood","Creativity","Nature"]', '丰盛和富足，母性的关怀，创造力的表达，与自然的连接。', 'Abundance and prosperity, maternal care, expression of creativity, connection with nature.', '创造力受阻，过度依赖，缺乏自信，忽视自我需求。', 'Blocked creativity, excessive dependence, lack of confidence, neglecting self-needs.', 'Earth', 'Venus', 'Venus', '皇后象征着丰盛、创造力和母性的力量。她提醒我们要珍惜生活中的美好，发挥自己的创造力，关爱他人和自己。', 'The Empress symbolizes abundance, creativity, and maternal power. She reminds us to cherish the beauty in life, express our creativity, and care for others and ourselves.');
INSERT OR REPLACE INTO tarot_cards (id, name_zh, name_en, suit, number, image_url, keywords_zh, keywords_en, meaning_upright_zh, meaning_upright_en, meaning_reversed_zh, meaning_reversed_en, element, astrological_sign, planet, description_zh, description_en) VALUES ('the_emperor', '皇帝', 'The Emperor', 'major', 4, '/images/tarot/major/emperor.jpg', '["权威","秩序","领导力","稳定"]', '["Authority","Order","Leadership","Stability"]', '强大的领导力，秩序和结构，权威和稳定，目标导向的行动。', 'Strong leadership, order and structure, authority and stability, goal-oriented action.', '过度控制，缺乏灵活性，滥用权威，独裁倾向。', 'Excessive control, lack of flexibility, abuse of authority, dictatorial tendencies.', 'Fire', 'Aries', 'Mars', '皇帝代表着权威、领导力和秩序。他提醒我们要承担责任，建立稳定的基础，以理性和目标为导向来管理生活。', 'The Emperor represents authority, leadership, and order. He reminds us to take responsibility, build stable foundations, and manage life with rationality and goal orientation.');
